import { uiActions } from "./ui-slice";

export const fetchCartData = () => {
    return (dispatch) => {
        const fetchData = async () => {
            const response = await fetch(
                'https://redux2-b2d57-default-rtdb.europe-west1.firebasedatabase.app/cart.json');

            if (!response.ok) {
                throw new Error('Could not fetch cart data.')
            }

            const data = await response.json();

            return data;
        }

    }
}


export const sendCartData = (cart) => {
    return async (dispatch) => {
        dispatch(
            uiActions.showNotification({
                status: 'pending',
                title: 'Sending...',
                message: 'Sending cart data.'
            })
        );

        const sendRequest = async () => {
            const response = await fetch('https://redux2-b2d57-default-rtdb.europe-west1.firebasedatabase.app/cart.json',
                {
                    method: 'PUT',
                    body: JSON.stringify(cart)
                })
            if (!response.ok) {
                throw new Error('Sending cart data failed.')
            }
        };

        try {
            await sendRequest();
            dispatch(
                uiActions.showNotification({
                    status: 'success',
                    title: 'Success!',
                    message: 'Send cart data successfully!'
                }))
        } catch (error) {
            dispatch(uiActions.showNotification({
                status: 'error',
                title: 'Error!',
                message: 'Error sending cart data'
            }))
        }
    };
};
